Page[154]=new Array("Microsoft OneDrive for Business is the online file storage application in the Office Groups suite. All Vertex employees are provided with a OneDrive folder for their own use. The personal OneDrive folder uses a cloud icon followed by OneDrive - Vertex, Inc (@). In addition to OneDrive folders for individual use, a OneDrive for Business folder is created for each Office Group that is created. The corporate OneDrive folder uses a building icon followed by Vertex, Inc (@). Note that this is a different location than the individual employee folders. The OneDrive for Business folder that is created with the Office Group is where the document libraries for that group are housed and files in those libraries are stored. It is not recommended that you synchronize a SharePoint folder with OneDrive. Synchronizing may cause problems, such as files always opening as read-only.","OneDrive","161674.htm");
Page[155]=new Array("It is not recommended that you synchronize a SharePoint folder with OneDrive. Synchronizing may cause problems, such as files always opening as read-only.","Synchronizing with OneDrive for Business","159236.htm");
var PageCount=156;

// "X:\AuthorIT\NewData\Templates\HTML No Frames\dhtml_search.js"
function search(SearchWord){
  console.log('search(' + SearchWord +') successfully called...rendering results next...');
var Result="";
var NrRes=0;

if(SearchWord.length>=1){
  console.log('SearchWord length is >= 1');
   SearchWord=SearchWord.toLowerCase();
   this.status="Searching, please wait...";
   Result+="<table border='0' cellpadding='5' class='searchResults' width='100%'>";
   for(j=0;j<PageCount;j++){
       k=Page[j].length-1; 
       for(i=0;i<k;i++){
           WordPos=Page[j][i].toLowerCase().indexOf(SearchWord);
           if(WordPos>-1){
               FoundWord=Page[j][i].substr(WordPos,SearchWord.length);
               NrRes++;
               Result+="<tr><td>";
               Result+="<a target='_self' href='"+Page[j][k]+"'>"+Page[j][k-1].replace(FoundWord,FoundWord.bold())+"</a><br/>\n";

               if(i<k-1){
                   if(Page[j][i].length>350){
                       Result+="..."+Page[j][i].substr(WordPos-100,200+FoundWord.length).replace(FoundWord,FoundWord.bold())+"...\n";
                   }
                   else{
                       Result+=Page[j][i].replace(FoundWord,FoundWord.bold())+"\n";
                   }
               }
               Result+="</td></tr>";
               break;
           }
       }
   }
   Result+="</table>";
   Result+="<p class='searchDetails'>&nbsp;" + NrRes + " result(s) found.</p>";
}

return Result;
}